// 프로필 선택 시
let flag = true;

$('.page-profile').click(function () {
    if (flag) {
        $('.page-dropdown').show();
        $('.pageBtn').attr('src', 'data:image/svg+xml;base64,');
    } else {
        $('.page-dropdown').hide();
        $('.pageBtn').attr('src', 'data:image/svg+xml;base64,')
    }
    flag = !flag;
})

$('.pageBtn').click(function () {
    if (flag) {
        $('.page-dropdown').show();
        $('.pageBtn').attr('src', 'data:image/svg+xml;base64,');
    } else {
        $('.page-dropdown').hide();
        $('.pageBtn').attr('src', 'data:image/svg+xml;base64,')
    }
    flag = !flag;
})

// 도시락 버튼 클릭 시
$('.moblieBtn').click(function(){
    $('.mobile-usermenu').css('transform', 'translateX(0)');
})

$('.close-button').click(function(){
    $('.mobile-usermenu').css('transform', '');
})